mapper.update:
UPDATE member SET
pw = #{pw}
WHERE id = #{id}

UPDATE fcryanboard
	SET viewcnt = viewcnt + 1
	WHERE bno = #{bno}

UPDATE fcryanboard SET title = #{title}, writer = #{writer},
filename = #{filename}, filesize = #{filesize},
content = #{content} WHERE bno = #{bno}

mapper.select:
SELECT * FROM fcryanreply
WHERE bno = #{bno}
ORDER BY rno ASC

mapper.update:
UPDATE fcryanboard SET
	replycnt = replycnt + 1
	WHERE bno = #{bno}

UPDATE fcryanboard SET
	replycnt = replycnt - 1
	WHERE bno = #{bno}
